mapper.sql:
FROM members a LEFT JOIN code b ON b.cdcSeq = a.mbrGbnCd LEFT JOIN code c ON c.cdcSeq = a.mbrClientCd LEFT JOIN code d ON d.cdcSeq = a.mbrGenderCd LEFT JOIN code e ON e.cdcSeq = a.mbrDeptCd LEFT JOIN code f ON f.cdcSeq = a.mbrPosCd WHERE 1 = 1
AND IFNULL(a.mbrDelNy, 0) = #{shDelNy}
AND a.mbrRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.mbrModDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.mbrName LIKE CONCAT('%', #{shValue}, '%') AND a.mbrClientName LIKE CONCAT('%', #{shValue}, '%')

mapper.select:
		SELECT aa.*
		  FROM (


		         ORDER BY a.mbrSeq         
				 LIMIT 99999999999
               ) aa
    	 LIMIT #{pgRowCount}
    	OFFSET #{pgOffset}

mapper.insert:
INSERT INTO members
	( 
	  mbrGbnCd
	, mbrClientCd
	, mbrClientName
	, mbrName
	, mbrGenderCd
	, mbrPhone
	, mbrTel
	, mbrBirthday
	, mbrDeptCd
	, mbrPosCd
	, mbrCompNo
	, mbrZip
	, mbrAddr
	, mbrAddrDetail
	, mbrEmail
	, mbrPassWord
	, mbrDelNy
	, mbrRegDt
	, mbrModDt
	)
VALUES
	(
	  #{mbrGbnCd}
	, #{mbrClientCd}
	, #{mbrClientName}
	, #{mbrName}
	, #{mbrGenderCd}
	, #{mbrPhone}
	, #{mbrTel}
	, #{mbrBirthday}
	, #{mbrDeptCd}
	, #{mbrPosCd}
	, #{mbrCompNo}
	, #{mbrZip}
	, #{mbrAddr}
	, #{mbrAddrDetail}
	, #{mbrEmail}
	, #{mbrPassWord}
	, 0
	, now()
	, now()
	)

mapper.update:
		UPDATE members
		   SET mbrGbnCd      = #{mbrGbnCd}
			 , mbrClientCd   = #{mbrClientCd}
			 , mbrClientName = #{mbrClientName}
			 , mbrName       = #{mbrName}
			 , mbrGenderCd   = #{mbrGenderCd}
			 , mbrPhone      = #{mbrPhone}
			 , mbrTel        = #{mbrTel}
			 , mbrBirthday   = #{mbrBirthday}
			 , mbrDeptCd     = #{mbrDeptCd}
			 , mbrPosCd      = #{mbrPosCd}
			 , mbrCompNo     = #{mbrCompNo}
			 , mbrZip        = #{mbrZip}
			 , mbrAddr       = #{mbrAddr}
			 , mbrAddrDetail = #{mbrAddrDetail}
			 , mbrEmail      = #{mbrEmail}
			 , mbrDelNy      = #{mbrDelNy}
			 , mbrModDt      = now()
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}

UPDATE members SET mbrModDt = now()
, mbrDelNy = 1 , mbrDelNy = 0
WHERE 1 = 1 AND mbrSeq = #{mbrSeq}

mapper.delete:
		DELETE FROM members
        WHERE 1 = 1		
          AND mbrSeq = #{mbrSeq}
