mapper.update:
update myorder
set
morWarehousingNy = #{morWarehousingNy}
where
	morSeq = #{morSeq}

mapper.select:
select @ROWNUM := @ROWNUM + 1 xRowNum ,a.motSeq ,b.morSeq ,b.morDate ,a.motCompCd ,c.pdtName ,a.motCount ,a.motAmt ,a.motWarehousingNy ,a.motDelNy ,a.motRegDt ,a.motModDt ,b.mbrSeq ,d.mbrClientName ,d.mbrName from myorderdetail a join myorder b on a.morSeq = b.morSeq join product c on a.pdtSeq = c.pdtSeq join members d on a.motCompCd = d.mbrSeq where 1=1 AND (@ROWNUM := 0) = 0 AND IFNULL(a.motDelNy, 0) = #{shDelNy} AND IFNULL(a.motWarehousingNy, 0) = #{shReceive}
AND b.morDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motModDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.motSeq = #{shValue} AND b.morSeq = #{shValue} AND c.pdtName LIKE CONCAT('%', #{shValue}, '%') AND d.mbrClientName LIKE CONCAT('%', #{shValue}, '%')
ORDER BY a.motSeq LIMIT #{pgRowCount} OFFSET #{pgOffset}

mapper.update:
update 
	myorderdetail
	set
		motCompCd = #{motCompCd}
		,motCount = #{motCount}
		,motAmt = #{motAmt}
		,motWarehousingNy = #{motWarehousingNy}
		,pdtSeq = #{pdtSeq}
	where 
		motSeq = #{motSeq}

mapper.select:
select count(*) from myorderdetail a join myorder b on a.morSeq = b.morSeq join product c on a.pdtSeq = c.pdtSeq join members d on a.motCompCd = d.mbrSeq where 1=1 AND IFNULL(a.motDelNy, 0) = #{shDelNy} AND IFNULL(a.motWarehousingNy, 0) = #{shReceive}
AND b.morDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motModDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.motSeq = #{shValue} AND b.morSeq = #{shValue} AND c.pdtName LIKE CONCAT('%', #{shValue}, '%') AND d.mbrClientName LIKE CONCAT('%', #{shValue}, '%')
