mapper.sql:
    	FROM orderdetail a
			join orders b on b.ordSeq = a.ordSeq
			join product c on c.pdtSeq = a.pdtSeq
			left join members d on d.mbrSeq = a.ortCompCd
			left join members e on e.mbrSeq = a.ortShippingCd
        WHERE 1=1
        AND ortDelNy = #{shDelNy}
        AND ortReleaseNy = #{shReleasedNy}


         	AND ordRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
        	AND ordModDt BETWEEN #{shDateStart} AND #{shDateEnd}
        	AND ordDate BETWEEN #{shDateStart} AND #{shDateEnd}
        	AND ortReleaseDt BETWEEN #{shDateStart} AND #{shDateEnd}



         	AND b.ordSeq = #{shValue}
         	AND c.pdtName LIKE CONCAT('%', #{shValue}, '%')
         	AND d.mbrClientName LIKE CONCAT('%', #{shValue}, '%')
         	AND d.mbrName LIKE CONCAT('%', #{shValue}, '%')
         	AND e.mbrClientName LIKE CONCAT('%', #{shValue}, '%')
         	AND e.mbrName LIKE CONCAT('%', #{shValue}, '%')

	FROM orders
    WHERE 1=1
    AND ordDelNy = #{shDelNy}
    AND ordReleasedNy = #{shReleasedNy}


     	AND ordRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
    	AND ordModDt BETWEEN #{shDateStart} AND #{shDateEnd}
    	AND ordDate BETWEEN #{shDateStart} AND #{shDateEnd}



     	AND ordSeq = #{shValue}

mapper.update:
update 
	orders
	SET
		ordSeq = #{ordSeq}
		,ordDate = #{ordDate}
		,ordModDt = now()
	WHERE 
		ortSeq = #{ortSeq}

update 
	orderdetail
	SET
		ortCompCd = #{ortCompCd}
		,ortCount = #{ortCount}
		,ortAmt = #{ortAmt}
		,ortShippingCd = #{ortShippingCd}
		,ortReleaseDt = #{ortReleaseDt}
		,ortReleaseNy = 1
		,ortDelNy = 0
		,ortModDt = now()
	WHERE 
		ortSeq = #{ortSeq}

mapper.insert:
INSERT INTO orderdetail
	( 
	  ortCompCd
	, ortCount
	, ortAmt
	, ortShippingCd
	, ortReleaseDt
	, ortReleaseNy
	, ortDelNy
	, ortRegDt
	, ortModDt
	, ordSeq
	, pdtSeq
	)
VALUES
	(
	  #{ortCompCd}
	, #{ortCount}
	, #{ortAmt}
	, #{ortShippingCd}
	, #{ortReleaseDt}
	, 0
	, 0
	, now()
	, now()
	, #{ordSeq}
	, #{pdtSeq}
	)

mapper.update:
UPDATE orders
   SET ordModDt = now()
     , ordReleasedNy = #{ordReleasedNy}
 WHERE ordSeq = #{ordSeq}
