mapper.sql:
FROM myorderdetail a JOIN myorder b ON b.morSeq = a.morSeq JOIN members d ON d.mbrSeq = a.motCompCd JOIN product e ON e.pdtSeq = a.pdtSeq LEFT JOIN warehousing c ON c.motSeq = a.motSeq WHERE 1 = 1
AND IFNULL(a.motDelNy, 0) = #{shDelNy}
AND b.morDate BETWEEN #{shDateStart} AND #{shDateEnd} AND c.whsDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.motModDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND b.morSeq LIKE CONCAT('%', #{shValue}, '%') AND d.mbrClientName LIKE CONCAT('%', #{shValue}, '%')

mapper.select:
		SELECT @ROWNUM := @ROWNUM + 1 xRowNum
             , aa.*
		  FROM (


		         ORDER BY b.morSeq, a.motSeq         
				 LIMIT 99999999999
               ) aa
         WHERE (@ROWNUM := 0) = 0
         ORDER BY @ROWNUM := @ROWNUM + 1 DESC 
    	 LIMIT #{pgRowCount}
    	OFFSET #{pgOffset}

mapper.update:
		UPDATE warehousing
		   SET whsCount = #{xwhsCountTot}
			 , whsModDt = now()
         WHERE 1 = 1		
           AND whsSeq = #{whsSeq}

UPDATE warehousing SET whsModDt = now()
, whsDelNy = 1 , whsDelNy = 0
WHERE 1 = 1 AND whsSeq = #{whsSeq}

mapper.delete:
		DELETE FROM warehousing
        WHERE 1 = 1		
          AND whsSeq = #{whsSeq}

mapper.update:
UPDATE myorderdetail
   SET motModDt = now()
     , motWarehousingNy = #{xwhsMotWarehousingNy}
 WHERE motSeq = #{xwhsMotSeq}

UPDATE myorder
   SET morModDt = now()
     , morWarehousingNy = #{xwhsMotWarehousingNy}
 WHERE morSeq = #{xwhsMorSeq}
